-- Table structure for biz_disease
-- ----------------------------
DROP TABLE IF EXISTS `biz_disease`;
CREATE TABLE `biz_disease` (
  `id` varchar(32) NOT NULL COMMENT '主键UUID',
  `first_letter` varchar(2) DEFAULT NULL COMMENT '首字符,分组字段',
  `code` varchar(50) DEFAULT NULL COMMENT '疾病代码',
  `name` varchar(200) DEFAULT NULL COMMENT '广安门医院命名的病名',
  `tcm_disease_name` varchar(500) DEFAULT NULL COMMENT '中医病名',
  `mm_disease_name` varchar(1500) DEFAULT NULL COMMENT '西医病名',
  `en_name` varchar(1500) DEFAULT NULL COMMENT '英文名称',
  `remark` text COMMENT '备注',
  `create_user` varchar(32) DEFAULT NULL COMMENT '创建者',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `update_user` varchar(32) DEFAULT NULL COMMENT '修改人',
  `update_time` datetime DEFAULT NULL COMMENT '修改时间',
  `order_num` int(11) DEFAULT NULL COMMENT '排序号:共菜单显示用',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

