-- ----------------------------
-- Table structure for biz_standard
-- ----------------------------
DROP TABLE IF EXISTS `biz_standard`;
CREATE TABLE `biz_standard` (
  `id` varchar(32) NOT NULL COMMENT '主键UUID',
  `disease_id` varchar(32) DEFAULT NULL COMMENT '疾病ID',
  `order_num` varchar(50) DEFAULT NULL COMMENT '序号(T01代表疾病名称，_001代表标准顺序号，如：F04_001)',
  `type` varchar(2) DEFAULT NULL COMMENT '标准类型：0中医标准,1西医标准',
  `cn_title` varchar(500) DEFAULT NULL COMMENT '中文题名',
  `en_title` varchar(500) DEFAULT NULL COMMENT '英文题名',
  `publish_org` varchar(500) DEFAULT NULL COMMENT '发布机构',
  `publish_date` int(11) DEFAULT NULL COMMENT '发布日期',
  `source` varchar(5000) DEFAULT NULL COMMENT '来源',
  `language` varchar(50) DEFAULT NULL COMMENT '全文语种',
  `type_stag` text COMMENT '分型分期',
  `mm_disease_name` varchar(500) DEFAULT NULL COMMENT '西医病名',
  `tcm_disease_name` varchar(500) DEFAULT NULL COMMENT '中医病名',
  `tcm_disease_dialectical` text COMMENT '中医辨证',
  `mm_standard` text COMMENT '西医诊断标准',
  `tcm_standard` text COMMENT '中医诊断标准',
  `effect_target` text COMMENT '疗效指标',
  `effect_standard` text COMMENT '疗效判定标准',
  `remark` text COMMENT '备注',
  `status` varchar(2) DEFAULT NULL COMMENT '发布状态（1:新建，2:待审核,3:审核通过,4:审核未通过,5:发布通过,6:发布未通过,7:发布撤销）',
  `is_del` varchar(2) DEFAULT NULL COMMENT '是否删除：0:正常,1:回收站',
  `create_user` varchar(100) DEFAULT NULL COMMENT '创建人',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `update_user` varchar(100) DEFAULT NULL COMMENT '最后更新人',
  `update_time` datetime DEFAULT NULL COMMENT '修改时间',
  PRIMARY KEY (`id`),
  KEY `disease_id` (`disease_id`) USING BTREE,
  CONSTRAINT `biz_standard_ibfk_1` FOREIGN KEY (`disease_id`) REFERENCES `biz_disease` (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
