DROP TABLE IF EXISTS `biz_suggest`;
CREATE TABLE `biz_suggest` (
  `id` varchar(32) NOT NULL COMMENT '主键UUID',
  `standard_id` varchar(32) NOT NULL COMMENT '标准ID',
  `type` varchar(2) DEFAULT NULL COMMENT '意见类型:0:提交审核,1:审核,2:发布,3:撤销',
  `content` text COMMENT '意见内容',
  `create_user` varchar(32) DEFAULT NULL COMMENT '发表人',
  `create_time` datetime DEFAULT NULL COMMENT '发表时间',
  `isPass` varchar(2) DEFAULT NULL COMMENT '1:通过,2:未通过',
  PRIMARY KEY (`id`),
  KEY `biz_suggest_ibfk_1` (`standard_id`) USING BTREE,
  CONSTRAINT `biz_suggest_ibfk_1` FOREIGN KEY (`standard_id`) REFERENCES `biz_standard` (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

