-- ----------------------------
-- Procedure structure for dealAttach
-- ----------------------------
DROP PROCEDURE IF EXISTS `dealAttach`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `dealAttach`()
BEGIN
      DECLARE Done INT DEFAULT 0;
     
			DECLARE t_id VARCHAR(60);  
			DECLARE t_name VARCHAR(500);  
			DECLARE t_order_num VARCHAR(100);  


      DECLARE rs CURSOR FOR SELECT id,cn_title,order_num from temp_biz_standard;

      DECLARE CONTINUE HANDLER FOR SQLSTATE '02000' SET Done = 1;
     

      OPEN rs;  
     

      FETCH NEXT FROM rs INTO t_id, t_name,t_order_num;     

      REPEAT
            IF NOT Done THEN
								
										BEGIN
															DECLARE ss_str VARCHAR(100);  
															DECLARE ss_next int DEFAULT 1;
															DECLARE ss_strCur VARCHAR(50); 

															set ss_str=  t_order_num ;
																		
															while ss_next <= Get_StrArrayLength(ss_str,';') do
								 
																set ss_strCur= Get_StrArrayStrOfIndex(ss_str,';',ss_next) ;

																	if LENGTH(ss_strCur)>0 THEN
																	END if;

																set ss_next=ss_next+1 ;
													

															end WHILE;             
										end;
								

            END IF;
           
		  FETCH NEXT FROM rs INTO t_id, t_name,t_order_num;     
 
      UNTIL Done END REPEAT;
     
      CLOSE rs;
 END
;;
DELIMITER ;

-- ----------------------------
-- Function structure for Get_StrArrayLength
-- ----------------------------
DROP FUNCTION IF EXISTS `Get_StrArrayLength`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` FUNCTION `Get_StrArrayLength`(ss_str varchar(255),ss_split varchar(10)) RETURNS int(11)
begin 
declare ss_location int; 
declare ss_start int; 
declare ss_length int; 
set ss_str=ltrim(rtrim(ss_str)); 
set ss_location=LOCATE(ss_split,ss_str); 
set ss_length=1; 
while ss_location<>0 do

set ss_start=ss_location+1; 
set ss_location=LOCATE(ss_split,ss_str,ss_start); 
set ss_length=ss_length+1;
end WHILE;
return ss_length; 
end
;;
DELIMITER ;

-- ----------------------------
-- Function structure for Get_StrArrayStrOfIndex
-- ----------------------------
DROP FUNCTION IF EXISTS `Get_StrArrayStrOfIndex`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` FUNCTION `Get_StrArrayStrOfIndex`(
ss_str VARCHAR(1024), 
ss_split VARCHAR(10), 
ss_index INT) RETURNS varchar(1024) CHARSET utf8
BEGIN

DECLARE ss_location INT ;
DECLARE ss_start INT ;
DECLARE ss_next INT ;
DECLARE ss_seed INT ;
SET ss_str=LTRIM(RTRIM(ss_str)) ;
SET ss_start=1 ;
SET ss_next=1 ;
SET ss_seed=LENGTH(ss_split) ;
SET ss_location=LOCATE(ss_split,ss_str) ;
WHILE ss_location<>0 AND ss_index>ss_next DO
 
SET ss_start=ss_location+ss_seed ;
SET ss_location=LOCATE(ss_split,ss_str,ss_start) ;
SET ss_next=ss_next+1 ;
END WHILE;
IF ss_location =0 THEN	set ss_location =LENGTH(ss_str)+1 ;
END IF;
RETURN SUBSTRING(ss_str,ss_start,ss_location-ss_start) ;
    END
;;
DELIMITER ;
