INSERT INTO `biz_standard` VALUES ('99', '18', 'J04_008', '1', '咳嗽的诊断与治疗指南（2009版）', '无', '中华医学会呼吸病学分会哮喘学组', '2009', '咳嗽的诊断与治疗指南(2009版)[J].中华结核和呼吸杂志,2009,32(6):407-413.', '中文', null, '急性气管-支气管炎', null, null, '临床表现：起病初期常有上呼吸道感染症状。随后咳嗽可渐加剧，伴或不伴咳痰，伴细菌感染者常咳黄脓痰。急性气管、支气管炎常呈自限性，全身症状可在数天内消失，但咳嗽、咳痰一般持续2～3周。x线检查无明显异常或仅有肺纹理增加。查体双肺呼吸音粗，有时可闻及湿性或干性啰音。<br />&nbsp;&nbsp;诊断：主要依据临床表现，要注意与流感、肺炎、肺结核、百日咳、急性扁桃体炎等疾病鉴别。 ', null, null, ' ', ' ', '1', '0', 'admin', null, 'admin', null);
